import{_ as e,c as t,o as a,a as d}from"./app.3817bf4e.js";const u='{"title":"\u81EA\u5B9A\u4E49 Agent \u76D1\u63A7\u9879\u76EE","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u81EA\u5B9A\u4E49 Agent \u76D1\u63A7\u9879\u76EE","slug":"\u81EA\u5B9A\u4E49-agent-\u76D1\u63A7\u9879\u76EE"}],"relativePath":"guide/q7.md","lastUpdated":1678322784000}',n={},o=d('<h2 id="\u81EA\u5B9A\u4E49-agent-\u76D1\u63A7\u9879\u76EE" tabindex="-1">\u81EA\u5B9A\u4E49 Agent \u76D1\u63A7\u9879\u76EE <a class="header-anchor" href="#\u81EA\u5B9A\u4E49-agent-\u76D1\u63A7\u9879\u76EE" aria-hidden="true">#</a></h2><h4 id="\u81EA\u5B9A\u4E49\u76D1\u63A7\u7684\u7F51\u5361\u548C\u786C\u76D8\u5206\u533A" tabindex="-1">\u81EA\u5B9A\u4E49\u76D1\u63A7\u7684\u7F51\u5361\u548C\u786C\u76D8\u5206\u533A <a class="header-anchor" href="#\u81EA\u5B9A\u4E49\u76D1\u63A7\u7684\u7F51\u5361\u548C\u786C\u76D8\u5206\u533A" aria-hidden="true">#</a></h4><ul><li>\u6267\u884C <code>/opt/nezha/agent/nezha-agent --edit-agent-config</code> \u6765\u9009\u62E9\u81EA\u5B9A\u4E49\u7684\u7F51\u5361\u548C\u5206\u533A\uFF0C\u7136\u540E\u91CD\u542F Agent \u5373\u53EF\u751F\u6548</li></ul><h4 id="\u5176\u4ED6\u8FD0\u884C\u53C2\u6570" tabindex="-1">\u5176\u4ED6\u8FD0\u884C\u53C2\u6570 <a class="header-anchor" href="#\u5176\u4ED6\u8FD0\u884C\u53C2\u6570" aria-hidden="true">#</a></h4><p>\u901A\u8FC7\u6267\u884C <code>./nezha-agent --help</code> \u67E5\u770B\u652F\u6301\u7684\u53C2\u6570\uFF0C\u5982\u679C\u4F60\u4F7F\u7528\u4E86\u4E00\u952E\u811A\u672C\u5B89\u88C5Agent\uFF0C\u53EF\u4EE5\u7F16\u8F91 <code>/etc/systemd/system/nezha-agent.service</code>\uFF0C\u5728 <code>ExecStart=</code> \u8FD9\u4E00\u884C\u7684\u672B\u5C3E\u52A0\u4E0A\u4EE5\u4E0B\u53C2\u6570</p><ul><li><code>--report-delay</code> \u63A7\u5236\u7CFB\u7EDF\u4FE1\u606F\u4E0A\u62A5\u7684\u95F4\u9694\uFF0C\u9ED8\u8BA4\u4E3A 1 \u79D2\uFF0C\u53EF\u4EE5\u8BBE\u7F6E\u4E3A 3 \u6765\u8FDB\u4E00\u6B65\u964D\u4F4E agent \u7AEF\u7CFB\u7EDF\u8D44\u6E90\u5360\u7528\uFF08\u914D\u7F6E\u533A\u95F4 1-4\uFF09</li><li><code>--skip-conn</code> \u4E0D\u76D1\u63A7\u8FDE\u63A5\u6570\uFF0C\u63A8\u8350 \u673A\u573A/\u8FDE\u63A5\u5BC6\u96C6\u578B \u670D\u52A1\u5668\u6216CPU\u5360\u7528\u8F83\u9AD8\u7684\u670D\u52A1\u5668\u8BBE\u7F6E</li><li><code>--skip-procs</code> \u4E0D\u76D1\u63A7\u8FDB\u7A0B\u6570\uFF0C\u4E5F\u53EF\u4EE5\u964D\u4F4E Agent \u5360\u7528</li><li><code>--disable-auto-update</code> \u7981\u6B62 <strong>\u81EA\u52A8\u66F4\u65B0</strong> Agent\uFF08\u5B89\u5168\u7279\u6027\uFF09</li><li><code>--disable-force-update</code> \u7981\u6B62 <strong>\u5F3A\u5236\u66F4\u65B0</strong> Agent\uFF08\u5B89\u5168\u7279\u6027\uFF09</li><li><code>--disable-command-execute</code> \u7981\u6B62\u5728 Agent \u4E0A\u6267\u884C\u5B9A\u65F6\u4EFB\u52A1\u3001\u6253\u5F00\u5728\u7EBF\u7EC8\u7AEF\uFF08\u5B89\u5168\u7279\u6027\uFF09</li><li><code>--tls</code> \u542F\u7528 SSL/TLS \u52A0\u5BC6\uFF08\u4F7F\u7528 nginx \u53CD\u5411\u4EE3\u7406 Agent \u7684 grpc \u8FDE\u63A5\uFF0C\u5E76\u4E14 nginx \u5F00\u542F SSL/TLS \u65F6\uFF0C\u9700\u8981\u542F\u7528\u8BE5\u9879\u914D\u7F6E\uFF09</li></ul>',6),c=[o];function i(r,s,l,g,h,_){return a(),t("div",null,c)}var A=e(n,[["render",i]]);export{u as __pageData,A as default};
